import React, { useState, useContext } from "react";
import { Navigate } from "react-router-dom";
import loginUser from "../services/loginUser.js";
import getUserData from "../services/user/getUserData";
import { Context, ToastVisibilityContext } from "../context/userContext";
import {
  ButtonLogin,
  FormFieldNameLabel,
  FormLogin,
  FormLoginDiv,
  LoginButtonGroupDiv,
} from "../styles/css.jsx";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCircleUser, faLockOpen } from "@fortawesome/free-solid-svg-icons";
import ToastMessage from "../components/ToastMessage.jsx";

const UserLogin = () => {
  const [username, setUserName] = useState(null);
  const [password, setPassword] = useState(null);
  const [userData, setUserData] = useState([]);

  const [
    toastVisible,
    setToastVisible,
    toastMessage,
    setToastMessage,
    toastType,
    setToastType,
  ] = useContext(ToastVisibilityContext);

  const { key, csfrToken, userLoggedId, userLoggedName } =
    useContext(Context);

  const [csfrTokenValue, setCsfrTokenValue] = csfrToken;
  const [userLoggedKey, setUserLoggedkey] = key;
  const [userLoggedIdValue, setUserLoggedValue] = userLoggedId;
  const [userLoggedNameValue, setUserLoggedNameValue] = userLoggedName;

  const getCsrfToken = () => {
    return document.cookie
      .split("; ")
      .find((row) => row.startsWith("csrftoken="))
      ?.split("=")[1];
  };

  const displayToast = (message, type) => {
    setToastMessage(message);
    setToastType(type);
    setToastVisible(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const csfrTokenInCookies = getCsrfToken();
    setCsfrTokenValue(csfrTokenInCookies);

    const tokenResponse = await loginUser({
      username,
      password,
    })
    .then((data) => {
      if (data.Error) {
        throw data;
      }
      return data;
    })


    window.localStorage.setItem("logedUserToken", tokenResponse.key);
    setUserLoggedkey(tokenResponse.key);


    const userDataResponse = await getUserData(username, csfrTokenValue , userLoggedKey)
    .then((data) => {
      return data
    })
  .catch((error) => {
    console.log("error", error);
    displayToast(error, "error");
  });

  setUserData(userDataResponse)

  const getUserDataResponse = userDataResponse => {
    for (let i = 0; i <= userDataResponse.length; i++) {
      const apiUsername = userDataResponse[i]["username"];
      if (username.toLowerCase() === apiUsername.toLowerCase()) {
        const logedUserId = userDataResponse[i]["id"];
        const logedserName = userDataResponse[i]["username"];
        window.localStorage.setItem("logedUserId", logedUserId);
        window.localStorage.setItem("logedUserName", logedserName);
        setUserLoggedValue(userDataResponse[i]["id"]);
        setUserLoggedNameValue(userDataResponse[i]["username"]);
        break;
      }

    setUserName(null);
    setPassword(null);
  };
  }

  getUserDataResponse(userData)


}
  return (
    <FormLoginDiv>
      {toastVisible && (
        <ToastMessage message={toastMessage} type={toastType} duration={3000} />
      )}
      <FontAwesomeIcon icon={faCircleUser} />
      <h2>User Login</h2>
      <FormLogin className="login-form-form" onSubmit={(e) => handleSubmit(e)}>
        <FormFieldNameLabel>
          <p>Username</p>
          <input 
          type="text" 
          required
          onChange={(e) => setUserName(e.target.value)} />
        </FormFieldNameLabel>
        <FormFieldNameLabel>
          <p>Password</p>
          <input
            type="password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
        </FormFieldNameLabel>
        {/* <p> { errorText }</p> */}
        <LoginButtonGroupDiv>
          <ButtonLogin type="submit">Login</ButtonLogin>
          <a href="/">
            <FontAwesomeIcon icon={faLockOpen} /> Go to see the menu
          </a>
        </LoginButtonGroupDiv>
      </FormLogin>
      {userLoggedKey && <Navigate to="/" replace={true} />}
    </FormLoginDiv>
  );
};

export default UserLogin;
