import React from "react";
import DishList from "./pages/DishList";
import UserLogin from "./pages/UserLogin";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from "./components/Header"
import { ToastVisibilityContext, UserContextProvider, } from './context/userContext';
import ToolBar from './components/ToolBar';
import { AppWrapper } from './styles/css';
import { useContext } from "react";
import ToastMessage from "./components/ToastMessage";

const App = () =>    
{ 
  const [toastVisible, setToastVisible, toastMessage, setToastMessage, toastType, setToastType] = useContext(ToastVisibilityContext)
  return (
  <AppWrapper>
    <div>
      {toastVisible && (
        <ToastMessage
          message={toastMessage}
          type={toastType}
          duration={3000}
        />
      )}
    </div>
    <UserContextProvider>
        <Router>
            <Routes>
                <Route path="/" element={
                <>
                <Header />
                <ToolBar />
                <DishList />
                </>
                } />
                <Route path="/login" element={<UserLogin />} />
            </Routes>
        </Router>
    </UserContextProvider>
  </AppWrapper> 
)}

export default App;
