import styled from "styled-components";
import React, { useEffect, useContext, Suspense, useState } from "react";
import CategoryItem from "../components/CategoryItem";
import { CategoriesContext, ToastVisibilityContext } from "../context/userContext";
import ToastMessage from "../components/ToastMessage";
import { DishListContentWrapper, DishListWrapper, LoadingElement } from "../styles/css";
import { fetchData } from "../services/fetchData";
import { DISH_CATEGORIES } from "../services/settings";
import DishItem from "../components/DishItem";
import { DishesVisibilityContextProvider } from "../context/dishesVisibilityContext";


const fetchCategories = fetchData(DISH_CATEGORIES)

const DishList = () => {

  const dishCategoriesApi = fetchCategories.read();

  const [toastVisible, setToastVisible, toastMessage, setToastMessage, toastType, setToastType] = useContext(ToastVisibilityContext)
  const [dishCategoriesState, setDishCategoriesState] = useState(dishCategoriesApi)

  // const previousDishesVisibleRef = useRef(dishesVisible);

  function DishCategoriesDetail({dishCategories}){

    const [dishesVisible, setDishesVisible] = useContext(DishesVisibilityContextProvider);

    return (
      <Suspense fallback={<LoadingElement>Loading...</LoadingElement>}>
        <CategoiesDiv key={1}>
          {dishCategories?.map((category, index) => (
            <>
              <CategoriesContext>
              <CategoryItem key={index} category={category} dishCategories={[dishCategoriesState, setDishCategoriesState]} dishesVisibilityState={[dishesVisible, setDishesVisible]}  />
              <DishListWrapper>
                {category.dishes &&
                category.dishes.map((dish, id) => (
                <DishItem key={id} visible={dishesVisible} dish={dish}  />
                ))}
            </DishListWrapper>
            </CategoriesContext>
            </>
          ))}
        </CategoiesDiv>
      </Suspense>
    );
  }

  useEffect(() => {
    // setDishCategories(dishCategoriesApi)
  }, [])

  return (
    <DishListContentWrapper>
      <div>
        {toastVisible && (
          <ToastMessage
            message={toastMessage}
            type={toastType}
            duration={3000}
          />
        )}
      </div>
      <DishCategoriesDetail key={2} dishCategories={dishCategoriesState} />

    </DishListContentWrapper>
  );
};

export default DishList;

const CategoiesDiv = styled.div``;
