import styled from "styled-components";
import React, { useEffect, useContext, Suspense, useState } from "react";
import CategoryItem from "../components/CategoryItem";
import { CategoriesContext, ToastVisibilityContext } from "../context/userContext";
import ToastMessage from "../components/ToastMessage";
import { DishListContentWrapper, LoadingElement } from "../styles/css";
import { fetchData } from "../services/fetchData";
import { DISH_CATEGORIES } from "../services/settings";



const fetchCategories = fetchData(DISH_CATEGORIES)

const DishList = () => {
  const ApiData = fetchCategories.read();

  // const [dishCategories, setDishCategories, categoriesLoading, setDishCategoriesLoading] = useContext(CategoriesContext);

  const [toastVisible, setToastVisible, toastMessage, setToastMessage, toastType, setToastType] = useContext(ToastVisibilityContext)
  const [dishCategories, setDishCategories] = useState(ApiData)



  function DishCategoriesDetail({dishCategories}){

    return (
      <Suspense fallback={<LoadingElement>Loading...</LoadingElement>}>
        <DishCategoriesApi
          dishCategories={dishCategories}
        />
      </Suspense>
    );
  }

  function DishCategoriesApi({dishCategories}) {
    console.log("dishCategories", dishCategories);
    // const data = dishCategories.read()
    return (
      <CategoiesDiv>
          {/* {console.log("dishCategories", dishCategories)} */}
          {dishCategories?.map((category, index) => (
            <>
              <CategoryItem key={String(category.id)} data={category} />
            </>
          ))}
        </CategoiesDiv>
    );
  }

  // const fetchApiData = () => {
  //   // const data = fetchCategories.read();
  //   setDishCategories(fetchCategories)
  // }


  useEffect(() => {
    // const apiData = fetchCategories;
    // const dishCategoriesApi = apiData.read();
    // setDishCategories(dishCategoriesApi())
    // fetchApiData();
  },[dishCategories]);

  return (
    <DishListContentWrapper>
      <div>
        {toastVisible && (
          <ToastMessage
            message={toastMessage}
            type={toastType}
            duration={3000}
          />
        )}
      </div>

      <DishCategoriesDetail dishCategories={dishCategories} />
    </DishListContentWrapper>
  );
};

export default DishList;

const CategoiesDiv = styled.div``;
