import styled from "styled-components";
import React, { useEffect, useContext, Suspense, useState } from "react";
import CategoryItem from "../components/CategoryItem";
import {
  CategoriesContext,
  ToastVisibilityContext,
} from "../context/userContext";
import ToastMessage from "../components/ToastMessage";
import {
  DishListContentWrapper,
  DishListWrapper,
  LoadingElement,
} from "../styles/css";
import { fetchData } from "../services/fetchData";
import { DISH_CATEGORIES } from "../services/settings";
import DishItem from "../components/DishItem";
import {
  DishesVisibilityContextProvider,
} from "../context/dishesVisibilityContext";

const fetchCategories = fetchData(DISH_CATEGORIES);

const DishList = () => {
  const dishCategoriesApi = fetchCategories.read();
  const [dishCategoriesContext, setDishCategoriesContext] =
    useContext(CategoriesContext);
  const [
    toastVisible,
    setToastVisible,
    toastMessage,
    setToastMessage,
    toastType,
    setToastType,
  ] = useContext(ToastVisibilityContext);

  const [dishCategoriesState, setDishCategoriesState] = useState(dishCategoriesApi);

  // const previousDishesVisibleRef = useRef(dishesVisible);

  function DishCategoriesDetail({ dishCategories }) {
    return (
      <Suspense fallback={<LoadingElement>Loading...</LoadingElement>}>
        <CategoiesDiv>
          {dishCategories?.map((category) => (
            <>
              <DishesVisibilityContextProvider>
                <CategoryItem
                  key={category.id}
                  category={category}
                  dishCategories={[dishCategoriesState, setDishCategoriesState]}
                />
                <DishListWrapper>
                  {category.dishes &&
                    category.dishes.map((dish) => (
                      <DishItem key={dish.id} dish={dish} />
                    ))}
                </DishListWrapper>
              </DishesVisibilityContextProvider>
            </>
          ))}
        </CategoiesDiv>
      </Suspense>
    );
  }

  useEffect(() => {
    setDishCategoriesContext(dishCategoriesState);
  }, [dishCategoriesState, dishCategoriesContext]);

  return (
    <DishListContentWrapper>
      <div>
        {toastVisible && (
          <ToastMessage
            message={toastMessage}
            type={toastType}
            duration={3000}
          />
        )}
      </div>
      <DishCategoriesDetail dishCategories={dishCategoriesState} />
    </DishListContentWrapper>
  );
};

export default DishList;

const CategoiesDiv = styled.div``;
